import classes from './CartItem.module.css';
import { useState } from 'react';
const CartItem = (props) => {
  const { title, quantity, total, price } = props.item;
  console.log(title,props.item)
  const TotalItem  = props.item
  let  [quant,setquant] = useState(parseInt(TotalItem.quantity))
  const hanldeincrement=(ele,item)=>{
    console.log(quant,typeof(quant))
    TotalItem[item].quantity=parseInt(TotalItem[item].quantity)
    setquant(quant+1)
  }
  const handledecrement=()=>{
    setquant(quant-1)
  }

  // if(quant===0){
  //   return('emptyCart')
  // }

  return(
    <>

      {TotalItem.map((ele,item)=>(


        <li className={classes.item}>
          <header>
            <h3>{ele.Tittle}</h3>
            <div className={classes.price}>
              {/* ${ele.total.toFixed(2)}{' '} */}
              {/* <span className={classes.itemprice}>(${ele.Price.toFixed(2)}/item)</span> */}
            </div>
          </header>
          <div className={classes.details}>
            <div className={classes.quantity}>
              x <span>{ele.quantity}</span>
            </div>
            <div className={classes.actions}>
              <button onClick={handledecrement(ele,item)}>-</button>
              <button onClick={hanldeincrement}>+</button>
            </div>
          </div>
        </li>    
      ))}
    </>
  );
};

export default CartItem;
